// Get the company size string from the previous node (e.g., "51-200 employees")
// CHANGE 'Enrich_Business' to the actual name of your enrichment node
const companySizeString = $('Enrich_Business').item.json.data.number_of_employees_range;

let employeeCount = 0;

if (companySizeString === 'Self-employed') {
  employeeCount = 1;

} else {
  // Remove " employees", "+", and all commas for clean parsing
  const cleanedString = companySizeString.replace(/ employees|\+|,/g, '');

  // Split the string by the hyphen to handle ranges like "51-200"
  const parts = cleanedString.split('-');

  // Take the last part of the split. For "51-200" this is "200". For "10001" this is "10001".
  const maxString = parts[parts.length - 1];

  // Convert the final string part into an integer number
  employeeCount = parseInt(maxString, 10);
}

let leadSegment = "Midmarket"

if (employeeCount < 50) {
  leadSegment = "SMB";
}

if (employeeCount > 500) {
  leadSegment = "Enterprise";
}
// Return the final number so the next nodes can use it
return {
  leadSegment
};
